import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, ViewBase,ImageBackground } from 'react-native';
import StartGameScreen from './screens/StartGameScreen';
import { LinearGradient } from 'expo-linear-gradient';
import { useState } from 'react';
import GameScreen from './screens/GameScreen';

export default function App() {
  const[useNumber,setUseNumbered]=useState(' ');
  const pickedNumberHandler=(pickedNumber)=>{
    setUseNumbered(pickedNumber);
    let screen=<StartGameScreen onConfirmNumber={pickedNumberHandler}/>
    if(useNumber)
    {screen=<GameScreen/>}
  }
  return (
<LinearGradient
        // Background Linear Gradient
        colors={['#72063c','yellow',]}
        style={styles.container}
      >
        <ImageBackground source={require('./assets/background.png')} resizeMode='cover'
        style={styles.container} imageStyle={{opacity:0.55}}>
    <StartGameScreen/>
    </ImageBackground>
    </LinearGradient>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,


  },
});
